package com.example.mealstock.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.mealstock.R;
import com.example.mealstock.adapters.RecipeListAdapter;
import com.example.mealstock.databinding.FragmentRecipeSearchBinding;
import com.example.mealstock.models.Recipe;

import java.util.ArrayList;
import java.util.List;

public class RecipeSearchFragment extends Fragment {

    private Button button;
    private RecyclerView recyclerView;
    private FragmentRecipeSearchBinding viewBinding;

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        viewBinding = FragmentRecipeSearchBinding.inflate(inflater, container, false);
        View view = viewBinding.getRoot();
        return view;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        setUpViews();
        setUpRecyclerView();

        button.setOnClickListener(new View.OnClickListener() {
            public void onClick(View v) {
                getParentFragmentManager().beginTransaction().replace(R.id.fragmentContainerView, FavoriteRecipeDetailFragment.class, null).addToBackStack("FavoriteRecipe").commit();
            }
        });
    }

    private void setUpViews() {
        button = viewBinding.recipeButton;
        recyclerView = viewBinding.recipeListView;
    }

    private void setUpRecyclerView(){
        recyclerView.setHasFixedSize(true);
        RecipeListAdapter recipeListAdapter = new RecipeListAdapter(requireContext(),  getUpExampleRecipeList());
        recyclerView.setAdapter(recipeListAdapter);
        LinearLayoutManager llm = new LinearLayoutManager(getActivity());
        recyclerView.setLayoutManager(llm);
    }

    private List<Recipe> getUpExampleRecipeList(){
        Recipe r1 = new Recipe();
        r1.setRecipeName("Nudeln mit Pesto");
        r1.setCookingTime("90 Minuten");
        r1.setImageUrl("https://img.chefkoch-cdn.de/rezepte/1887451306870540/bilder/488135/crop-960x720/gebratene-nudeln-mit-cashewnuessen-und-oliven-an-gruenem-pesto.jpg");
        Recipe r2 = new Recipe();
        r2.setRecipeName("Nudeln mit Tomatenketchup");
        r2.setCookingTime("5 Minuten");
        r2.setImageUrl("data:image/jpeg;base64,");
        List<Recipe> recipeList = new ArrayList<>();
        recipeList.add(r1);
        recipeList.add(r2);
        return recipeList;
    }
}